import React from 'react';
import './../App.css' ;
import {makeStyles } from '@material-ui/core';
import {Grid,Paper} from "@material-ui/core";
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Avatar from '@material-ui/core/Avatar';
import { Typography } from '@material-ui/core';
import  Tap  from './TapView';
import Profile  from './Profile';
import Button from '@material-ui/core/Button';


const useStyles = makeStyles((theme) =>({
  root: {
    flexGrow: 1,
    marginLeft: '270px',
    marginRight:'15px',

  },
  paper: {
    // padding: theme.spacing(8),

    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  another:{
    marginTop:'100px',
  },
 avatars:{
   marginTop:'90px',
   width:'80%',
  height:'100%',
  borderTop: 'blue solid 3px',
 },
 large: {
  width: theme.spacing(9),
  height: theme.spacing(9),
  marginLeft:'80px',
  marginTop:'30px',

},
gridone:{
  maxWidth: 500,

},
typo:{
  marginTop:'110px',
  marginLeft:'90px',

},
h2:{
  marginLeft:'50px',
},
}));

function Viewdirect () {
  const classes = useStyles();
  // {value === 0 && <TabContainer>Item One</Profile>}

  const [value, setValue] = React.useState(2);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };
  return (
    <div className={classes.root}>
      <Tap/>
    <Grid container spacing={3}>
          <Grid item xs={4} className={classes.gridone}>
             <Paper className={classes.paper, classes.avatars} spacing= {6}> 
             <Avatar src="/broken-image.jpg" className={classes.large} />
            
             <Typography variant="h4" className={classes.h2}>
                John Doe
                </Typography>
               <Typography variant="p"  className={classes.typo}>
                Patient
                </Typography>
                

                <Typography variant="h6" className={classes.h2}>
                Current Balance
                </Typography>
                <Typography variant="p"  className={classes.typo}>
                #45000
                </Typography>

                <Button variant="outlined" size="medium" color="primary" className={classes.margin}>
                  Medium
                </Button>
                <Button variant="outlined" size="medium" color="primary" className={classes.margin}>
                  Medium
                </Button>
          
            </Paper>

           
         
        </Grid>

        <Grid item xs={8}>
          <Paper className={classes.paper, classes.another} spacing= {10}>
                <Tabs
                  value={value}
                  indicatorColor="primary"
                  textColor="primary"
                  onChange={handleChange}
                  aria-label="disabled tabs example"
                >
                  <Tab label="Profile"  onClick={Profile}/>
                  <Tab label="Transaction" disabled />
                  <Tab label="Health Report" />
                  <Tab label="Activity Log" />

                </Tabs>
          </Paper>
        </Grid>
      </Grid>
 
      </div>
   
    
  );
}

export default Viewdirect;
