import React, { useState } from 'react';
import '../../App.css';
import { BrowserRouter } from "react-router-dom";


 const TopBarNavs = (props) =>{
    const {handletoggle, history} = props
    const {activeItem, setActiveItem} = useState('home')

    //navigation menu options
    const navOptions = [
        "Home",
        "Services",
        "Product",
        "About Us",
        "Contact",
        "Login",

    ]

    const handleClick = (e) =>{

        handletoggle && handletoggle()

        let clickedNav = e.target.getAttribute("nav")
        setActiveItem(clickedNav)

        clickedNav === 'Home'
            ? history.push('/')
             : history.push(`${clickedNav}`)
    }

    return (
        navOptions.map(option => <div className = {'nav-option' + (activeItem === option ? "clicked" : '')}
        onClick = {handleClick}
        nav = {option}
        key = {option}
            >
            {option}
        </div>        
        
        )
    )

}


export default TopBarNavs;
